                                $("#badge-corner").html(Number(str.qty)+Number(n));
                            }else {
                                mui.toast("该商品已经售罄，请选择其他商品！");
                            }

                        },'json'
                );

            }else {
                mui.openWindow({url: href})
            }

        })
    }

</script>
